package forloop;
import java.util.Scanner;
public class Pattern1_123_12345 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=0;
		for(int i=1;i<=7;i++)
		{
			for(int j=1;j<=i+x;j++)
			{
				System.out.print(j);
			}
			System.out.println("");
		}
		

	}

}
