package forlooping;

public class Ques8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     for(char ch='a';ch<='e';ch++)

	{
    	 for(char ch1='e';ch1>=ch;ch1--)
    	 {
    		System.out.print("");
    	 }

    	 for(char ch2='a';ch2<=ch;ch2++)
    	 {
    		 System.out.print(ch2);
    		 
    	 }
    	 System.out.println();
	}

}
}
